document.addEventListener("DOMContentLoaded",(function(){$("#sections").on("change",(function(){event.preventDefault(),$.ajax({method:"GET",url:`http://api.nytimes.com/svc/topstories/v2/${this.value}.json?api-key=PeYd6hAa0VOUHAOoc76PvocokqAM86gd&`,dataType:"json"}).done((function(e){$(".stories").empty();const t=e.results.filter((function(e){return"undefined"!==e.multimedia[4]})).slice(0,12);$.each(t,(function(e,t){$(".stories").append(`<article class='article-block' style="background-image: url('${t.multimedia[4].url}')">\n                <div class='article-text'><p>${t.abstract}</p></div></article>`)}))})).fail((function(){$(".stories").append("Sorry, the world has ended and there are no more news.")})).always((function(){}))}))}));